import { Component, OnInit, Input, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatPaginator, PageEvent} from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { Observable } from 'rxjs';
import { Imagem } from '../../_interfaces/interfaces.service';
import { ResponseService } from 'src/app/_config/response.service';
import { ImagemService } from '../../_services/imagem.service';
import { CrudService } from '../../_services/crud.service';
import { MatDialog } from '@angular/material/dialog';
import { ModalComponent } from '../../components/modal/modal.component';
import { base64StringToBlob } from 'blob-util';

@Component({
  selector: 'app-imagens',
  templateUrl: './imagens.component.html',
  styleUrls: ['./imagens.component.css']
})
export class ImagensComponent implements OnInit {
	//Formulário
  dataForm: FormGroup;
  updateForm: FormGroup;

  //Controlar a exibição da view
  //1 - Imagens, 2 - Adicionar, 3 - Editar
  openViewAction: number = 1;

  //Dados
  data: any;
  file: any;
  dataGetByID: any;

  //Table
  dataSource: MatTableDataSource<Imagem>;
  displayed = ['nome', 'path', 'acoes'];
  pageSizeOptions = [5, 10];

  //Paginação
  @ViewChild(MatPaginator) paginator: MatPaginator;
  @ViewChild(MatSort) sort: MatSort;

  constructor(
    public responseSrv: ResponseService,
    private imagemSrv: ImagemService,
    private crudSrv: CrudService,
    private dialog: MatDialog,
    private formBuilder: FormBuilder,
  ) {
    this.dataForm = this.formBuilder.group({
      name: ['', Validators.required],
      path: ['', Validators.required],
    });

    this.updateForm = this.formBuilder.group({
      name: ['', Validators.required],
    });
  }

  ngOnInit(): void {

    // //Se estiver na view de exibir, carregar admins
    if(this.openViewAction === 1){
      this.get();
    }
  }

  //Controle de views
  openView(view: number, id: any) {
    this.openViewAction = view;

    if(view === 3 && id != null){
      this.getByID(id);
      this.openViewAction = view;
    }

    if(view === 1){
      this.get();
    }
  }

  get() {
    this.crudSrv.get('imagens').subscribe(
    res => {
      this.data = res;

      this.dataSource = new MatTableDataSource(this.data);
      this.dataSource.paginator = this.paginator;
      this.dataSource.sort = this.sort;

    },
    err => {
    	this.responseSrv.handleErrorStatusCode(err.status, err.error, 0);
    });
  }

  getByID(id: number){
    this.crudSrv.getByID('imagens', id).subscribe(
      res => {
        this.dataGetByID = res;
      },
      err => {
        console.log(err);
      },
    );
  }

  fileSelected(e: any){
    if(e.target.files) {
      var reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);
      reader.onload = (event: any) => {
        let teste = this.b64ToBlob(event.target.result);
        console.log(teste);
      }
    }
  }

  b64ToBlob(base64: string) {
    return fetch(base64).then(async res => {
      await res.blob();
      return res.blob();
    });
  }

  post(){
    //Reseta os erros até o momento
    this.responseSrv.alertResponse = [];

    //Caso o formulário seja inválido, não vai enviar
    if(!this.dataForm.valid){
      return;
    }
    console.log(this.file);
    //this.dataForm.value.path = this.file;
    //this.imagemSrv.post(this.dataForm.value);

    // this.imagemSrv.post(this.dataForm.value).subscribe(
    //   res => {

    //     this.responseSrv.handleSuccess(res, 1);

    //     //Resetar o formulário
    //     this.dataForm.reset();
    //     this.file = [];
    //   },
    //   err => {
    //     this.responseSrv.handleErrorStatusCode(err.status, err.error, 1);
    //   });
  }

  update(id: number){
    //Reseta os erros até o momento
    this.responseSrv.alertResponse = [];

    this.crudSrv.update('imagens', id, this.updateForm.value).subscribe(
      res => {
        this.responseSrv.handleSuccess(res, 2);

        //Resetar o formulário
        this.dataForm.reset();
      },
      err => {
        //this.responseSrv.handleError(err, 2);
        this.responseSrv.handleErrorStatusCode(err.status, err.error, 2);
      });
  }

  openModalDelete(id: number){

    const dialogRef = this.dialog.open(ModalComponent, {
      width: '350px',
    });

    dialogRef.afterClosed().subscribe(result => {
      if(result === true){
        this.crudSrv.delete('imagens', id).subscribe(
          res => {
            this.openView(1, null);
          },
          err => {
            console.log(err);
          },
        );

      }

      return;
    });
  }

}
<!-- Header -->
<div class="header bg-primary pb-6">
  <div class="container-fluid">
    <div class="header-body">
      <div class="row align-items-center py-4">
        <div class="col-lg-8 col-7">
          <nav aria-label="breadcrumb" class="d-none d-md-inline-block">
            <ol class="breadcrumb breadcrumb-links breadcrumb-dark">
              <li class="breadcrumb-item"><a routerLink="/administrativo/perfil"><i class="fas fa-home"></i></a></li>
              <li class="breadcrumb-item active" aria-current="page">Imagens</li>
            </ol>
          </nav>
        </div>
        <div class="col-lg-4 col-5 text-right">
          <a (click)="openView(2, null)" class="btn btn-sm btn-neutral">Adicionar</a>
          <a href="#" class="btn btn-sm btn-neutral">Filtros</a>
        </div>
      </div>
    </div>
  </div>
</div>

<!-- Page content -->
<div class="container-fluid mt--6">
  <!-- Listar -->
  <ng-container *ngIf="openViewAction === 1" class="row">
    <div class="col">
      <div class="card">
        <!-- Card header -->
        <div class="card-header border-0">
          <h3 class="mb-0">Imagens</h3>
        </div>
        <!-- Light table -->
        <div class="table-responsive">
          <table mat-table [dataSource]="dataSource" matSort class="table align-items-center table-flush">
            <ng-container matColumnDef="nome">
              <th mat-header-cell *matHeaderCellDef mat-sort-header class="sort table-name">Nome</th>
              <td mat-cell *matCellDef="let row"> {{row.name}}</td>
            </ng-container>

            <ng-container matColumnDef="path">
              <th mat-header-cell *matHeaderCellDef mat-sort-header class="sort">Imagem</th>
              <td mat-cell *matCellDef="let row" class="box-img">
                <img src="/storage/media/{{row.path}}">
              </td>
            </ng-container>


            <ng-container matColumnDef="acoes">
              <th mat-header-cell *matHeaderCellDef mat-sort-header class="sort"></th>
              <td mat-cell *matCellDef="let row" class="text-right">
                <div class="dropdown">
                  <a class="btn btn-sm btn-icon-only text-light" href="#" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    <i class="fas fa-ellipsis-v"></i>
                  </a>
                  <div class="dropdown-menu dropdown-menu-right dropdown-menu-arrow">
                    <a href="/storage/media/{{row.path}}" target="_blank" class="dropdown-item item-view">
                      <i class="fas fa-eye"></i>
                      <span>Visualizar</span>
                    </a>

                    <a (click)="openView(3, row.id)" class="dropdown-item item-edit">
                      <i class="fas fa-edit"></i>
                      <span>Editar</span>
                    </a>
                    <a (click)="openModalDelete(row.id)" class="dropdown-item item-delete">
                      <i class="fas fa-trash"></i>
                      <span>Deletar</span>
                    </a>
                  </div>
                </div>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayed"></tr>
            <tr mat-row *matRowDef="let row; columns: displayed;"></tr>

            <tr class="mat-row" *matNoDataRow>
              <td class="mat-cell" colspan="4">Sem dados cadastrados.</td>
            </tr>
          </table>
        </div>

        <!-- Card footer -->
        <div class="card-footer py-4">
          <mat-paginator
            [pageSizeOptions]="pageSizeOptions"
            >
          </mat-paginator>
        </div>
      </div>
    </div>
  </ng-container>

  <!--  Adicionar -->
  <ng-container *ngIf="openViewAction === 2" class="row">
    <div class="col p-0">
      <div class="card shadow">
        <div class="card-header border-0">
            <div class="row align-items-center">
                <div class="col-8">
                    <h3 class="mb-0">Adicionar Imagem</h3>
                </div>
                <div class="col-4 text-right">
                    <a (click)="openView(1, null)" class="btn btn-sm btn-primary">Voltar</a>
                </div>
            </div>
        </div>
        <div class="card-body">
          <form [formGroup]="dataForm" (ngSubmit)="post()" >
            <h6 class="heading-small text-muted mb-4">Informações da Imagem</h6>

            <!-- Alertas -->
            <alert class="alert-dismiss" type="{{responseSrv.alertType}}"
            [isOpen]="responseSrv.open"
            [dismissible]="responseSrv.dismissible"
            (onClosed)="responseSrv.onClosed()"
            >
              <ng-container *ngFor="let mensagem of responseSrv.alertResponse">
                <strong>{{mensagem}}</strong><br />
              </ng-container>
            </alert>

            <div class="pl-lg-3">
              <div class="row">
                <div class="col-lg-6">
                  <div class="form-group">
                    <label class="form-control-label" for="input-first-name">Nome</label>
                    <input type="text" formControlName="name" class="form-control" placeholder="WhatsApp">
                    <span class="text-warning error" *ngIf="dataForm.controls.name.invalid">
                      Digite um nome válido.
                    </span>
                  </div>
                </div>

                <div class="col-lg-6">
                  <div class="form-group">
                    <label class="form-control-label" for="input-first-name">Arquivo</label>
                    <input type="file" formControlName="path" (change)="fileSelected($event)" class="form-control" >
                    <span class="text-warning error" *ngIf="dataForm.controls.path.invalid">
                      Insira um arquivo.
                    </span>
                  </div>
                </div>
              </div>
            </div>
            <div class="text-center">
              <button type="submit" [disabled]="dataForm.invalid" class="btn btn-success">Enviar</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  </ng-container>

   <!--  Editar -->
  <ng-container *ngIf="openViewAction === 3" class="row">
    <div class="col p-0">
      <div class="card shadow">
        <div class="card-header border-0">
            <div class="row align-items-center">
                <div class="col-8">
                    <h3 class="mb-0">Editar Imagem</h3>
                </div>
                <div class="col-4 text-right">
                    <a (click)="openView(1, null)" class="btn btn-sm btn-primary">Voltar</a>
                </div>
            </div>
        </div>
        <div class="card-body">
          <form [formGroup]="updateForm" (ngSubmit)="update(dataGetByID.id)">
            <h6 class="heading-small text-muted mb-4">Informações da Imagem</h6>

            <!-- Alertas -->
            <alert class="alert-dismiss" type="{{responseSrv.alertType}}"
            [isOpen]="responseSrv.open2"
            [dismissible]="responseSrv.dismissible"
            (onClosed)="responseSrv.onClosed()"
            >
              <ng-container *ngFor="let mensagem of responseSrv.alertResponse">
                <strong>{{mensagem}}</strong><br />
              </ng-container>
            </alert>

            <div class="pl-lg-3" *ngIf="dataGetByID">
              <div class="row">
                <div class="col-lg-11">
                  <div class="form-group">
                    <label class="form-control-label" for="input-first-name">Nome</label>
                    <span class="value-get-id" for="input-first-name">Atual: {{dataGetByID.name}}</span>
                    <input type="text" formControlName="name" class="form-control" placeholder="Foto tirada na aula de dança do dia 22/07">
                    <span class="text-warning error" *ngIf="updateForm.controls.name.invalid">
                      Digite um nome válido.
                    </span>
                  </div>
                </div>
              </div>
            </div>
            <div class="text-center">
              <button type="submit" [disabled]="updateForm.invalid" class="btn btn-success">Enviar</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  </ng-container>
</div>
